descriptor.templateCss:
.error {
    font-size: 14px !important;
    color: maroon;/*rgb(250,250,250);*/
    background-color: transparent;
    padding: 6px;
}

.error span {
    margin: auto;
}

.gpio-panel {
    padding-top: 10px;
    white-space: nowrap;
}

.gpio-panel section[fxflex] {
    min-width: 0px;
}


.switch-panel {
    margin: 0;
    height: 32px;
    width: 66px;
    min-width: 66px;
}

.switch-panel mat-slide-toggle {
    margin: 0;
    width: 36px;
    min-width: 36px;
}

.switch-panel.col-0 mat-slide-toggle {
    margin-left: 8px;
    margin-right: 4px;
}

.switch-panel.col-1 mat-slide-toggle {
    margin-left: 4px;
    margin-right: 8px;
}

.gpio-row {
    height: 32px;
}

.pin {
    margin-top: auto;
    margin-bottom: auto;
    color: white;
    font-size: 12px;
    width: 16px;
    min-width: 16px;
}

.switch-panel.col-0 .pin {
    margin-left: auto;
    padding-left: 2px;
    text-align: right;
}

.switch-panel.col-1 .pin {
    margin-right: auto;

    text-align: left;
}

.gpio-left-label {
    margin-right: 8px;
}

.gpio-right-label {
    margin-left: 8px;
}

descriptor.controllerScript:
var namespace;
var cssParser = new cssjs();

self.onInit = function() {
    var utils = self.ctx.$injector.get(self.ctx.servicesMap.get('utils'));
    namespace = 'gpio-control-' + utils.guid();
    cssParser.testMode = false;
    cssParser.cssPreviewNamespace = namespace;
    self.ctx.$container.addClass(namespace);
    self.ctx.ngZone.run(function() {
       init(); 
    });
}

function init() {

    var i, gpio;
    var scope = self.ctx.$scope;
    var settings = self.ctx.settings;
    scope.gpioList = [];
    for (var g = 0; g < settings.gpioList.length; g++) {
        gpio = settings.gpioList[g];
        scope.gpioList.push(
            {
                row: gpio.row,
                col: gpio.col,
                pin: gpio.pin,
                label: gpio.label,
                enabled: false
            }
        );
    }

    scope.requestTimeout = settings.requestTimeout || 1000;

    scope.switchPanelBackgroundColor = settings.switchPanelBackgroundColor || tinycolor('green').lighten(2).toRgbString();

    scope.gpioStatusRequest = {
        method: "getGpioStatus",
        paramsBody: "{}"
    };

    if (settings.gpioStatusRequest) {
        scope.gpioStatusRequest.method = settings.gpioStatusRequest.method || scope.gpioStatusRequest.method;
        scope.gpioStatusRequest.paramsBody = settings.gpioStatusRequest.paramsBody || scope.gpioStatusRequest.paramsBody;
    }

    scope.gpioStatusChangeRequest = {
        method: "setGpioStatus",
        paramsBody: "{\n   \"pin\": \"{$pin}\",\n   \"enabled\": \"{$enabled}\"\n}"
    };

    if (settings.gpioStatusChangeRequest) {
        scope.gpioStatusChangeRequest.method = settings.gpioStatusChangeRequest.method || scope.gpioStatusChangeRequest.method;
        scope.gpioStatusChangeRequest.paramsBody = settings.gpioStatusChangeRequest.paramsBody || scope.gpioStatusChangeRequest.paramsBody;
    }

    scope.parseGpioStatusFunction = "return body[pin] === true;";

    if (settings.parseGpioStatusFunction && settings.parseGpioStatusFunction.length > 0) {
        scope.parseGpioStatusFunction = settings.parseGpioStatusFunction;
    }

    scope.parseGpioStatusFunction = new Function("body, pin", scope.parseGpioStatusFunction);

    function requestGpioStatus() {
        self.ctx.controlApi.sendTwoWayCommand(scope.gpioStatusRequest.method, 
                            scope.gpioStatusRequest.paramsBody, 
                            scope.requestTimeout)
            .subscribe(
                function success(responseBody) {
                    for (var g = 0; g < scope.gpioList.length; g++) {
                        var gpio = scope.gpioList[g];
                        var enabled = scope.parseGpioStatusFunction.apply(this, [responseBody, gpio.pin]);
                        gpio.enabled = enabled;   
                        self.ctx.detectChanges();
                    }
                }
            );
    }

    function changeGpioStatus(gpio) {
        var pin = gpio.pin + '';
        var enabled = !gpio.enabled;
        enabled = enabled === true ? 'true' : 'false';
        var paramsBody = scope.gpioStatusChangeRequest.paramsBody;
        var requestBody = JSON.parse(paramsBody.replace("\"{$pin}\"", pin).replace("\"{$enabled}\"", enabled));
        self.ctx.controlApi.sendTwoWayCommand(scope.gpioStatusChangeRequest.method, 
                                    requestBody, scope.requestTimeout)
                    .subscribe(
                        function success(responseBody) {
                            var enabled = scope.parseGpioStatusFunction.apply(this, [responseBody, gpio.pin]);
                            gpio.enabled = enabled;
                            self.ctx.detectChanges();
                        }
                    );
    }

    scope.gpioCells = {};
    var rowCount = 0;
    for (i = 0; i < scope.gpioList.length; i++) {
        gpio = scope.gpioList[i];
        scope.gpioCells[gpio.row+'_'+gpio.col] = gpio;
        rowCount = Math.max(rowCount, gpio.row+1);
    }

    scope.prefferedRowHeight = 32;
    scope.rows = [];
    for (i = 0; i < rowCount; i++) {
        var row = [];
        for (var c =0; c<2;c++) {
            if (scope.gpioCells[i+'_'+c]) {
                row[c] = scope.gpioCells[i+'_'+c];
            } else {
                row[c] = null;
            }
        }
        scope.rows.push(row);
    }

    scope.gpioClick = function($event, gpio) {
        if (scope.rpcEnabled && !scope.executingRpcRequest) {
            changeGpioStatus(gpio);
        }
    };

    scope.gpioToggleChange = function($event, gpio) {
        gpio.enabled = !$event.checked;
        $event.source.toggle();
        self.ctx.detectChanges();
    }

    if (scope.rpcEnabled) {
        requestGpioStatus();   
    }

    self.onResize();
}

self.onResize = function() {
    var scope = self.ctx.$scope;
    var rowCount = scope.rows.length;
    var prefferedRowHeight = (self.ctx.height - 35)/rowCount;
    prefferedRowHeight = Math.min(32, prefferedRowHeight);
    prefferedRowHeight = Math.max(12, prefferedRowHeight);
    scope.prefferedRowHeight = prefferedRowHeight;
    var ratio = prefferedRowHeight/32;

    var css = '.mat-slide-toggle .mat-slide-toggle-bar {\n' +
        '   height: ' + 14*ratio+'px;\n'+
        '   width: ' + 36*ratio+'px;\n'+
    '}\n';
    css += '.mat-slide-toggle .mat-slide-toggle-thumb-container {\n' +
        '   height: ' + 20*ratio+'px;\n'+
        '   width: ' + 20*ratio+'px;\n'+
    '}\n';
    css += '.mat-slide-toggle .mat-slide-toggle-thumb {\n' +
        '   height: ' + 20*ratio+'px;\n'+
        '   width: ' + 20*ratio+'px;\n'+
    '}\n';
    css += '.mat-slide-toggle .mat-slide-toggle-ripple {\n' +
        '   height: ' + 40*ratio+'px;\n'+
        '   width: ' + 40*ratio+'px;\n'+
        '   top: calc(50% - '+20*ratio+'px);\n'+
        '   left: calc(50% - '+20*ratio+'px);\n'+
    '}\n';
    css += '.gpio-left-label, .gpio-right-label {\n' +
        '   font-size: ' + 16*ratio+'px;\n'+
    '}\n';
    var pinsFontSize = Math.max(9, 12*ratio);
    css += '.pin {\n' +
        '   font-size: ' + pinsFontSize+'px;\n'+
    '}\n';

    cssParser.createStyleElement(namespace, css);

    self.ctx.detectChanges();
}

self.onDestroy = function() {
}
